mapper.update:
update tb_product
   set stock = stock + #{productCnt}
 where product_id = #{productId}

update tb_product
   set stock = stock - #{productCnt}
 where product_id = #{productId}

mapper.select:
select count(*)
  from tb_coupon
 where name = #{name}

select count(*)
  from tb_coupon
 where no = #{no}

select count(*)
  from tb_product
 where product_id = #{productId}
