mapper.select:
		select o.*, e.sumPrice, e.countOfProducts
		from (select sum(price) as sumPrice, count(*) as countOfProducts
				from tb_order
                where order_id = #{orderId}
                group by order_id) e, tb_order o
		where order_id = #{orderId};

mapper.update:
update tb_order
set delievery_status = #{delieveryStatus}
where order_id = #{orderId}
