mapper.insert:
insert into tb_qna (
	 qna_title, qna_writer, qna_content
) values (
	 #{qnaTitle}, #{qnaWriter}, #{qnaContent}
)

mapper.update:
update tb_qna 
   set view_cnt = view_cnt + 1
 where qna_no = #{qnaNo}

mapper.select:
select view_cnt
  from tb_qna
 where qna_no = #{qnaNo}

mapper.update:
update tb_qna
   set qna_title = #{qnaTitle},
       qna_writer = #{qnaWriter}, 
       qna_content = #{qnaContent}
 where qna_no = #{qnaNo}
