mapper.update:
update tb_reviewboard 
   set views = views + 1
 where no = #{no}

update tb_reviewboard  
   set title = #{title}
   	   content = #{content}
 where no = #{no}

mapper.insert:
insert into tb_reviewboard (
	title, content
) values (
	#{title}, #{content}
);

insert into tb_reviewcomment(
	 no, writer, content
) values (
	 #{no}, #{writer}, #{content}
)

mapper.update:
update tb_reviewcomment
   set content = #{content}
 where comment_no = #{commentNo}

mapper.select:
select *
from reviewboardfile
where no = #{no}
and PATH is not null;
