mapper.insert:
insert into tb_board (
	 title, writer, content
) values (
	 #{title}, #{writer}, #{content}
)

mapper.update:
update tb_board 
   set view_cnt = view_cnt + 1
 where no = #{no}

update tb_board 
   set title = #{title},
       writer = #{writer}, 
       content = #{content}
 where no = #{no}
