mapper.select:
select *
  from tb_user
 where email = #{email}
   and pass = #{pass}

mapper.insert:
insert into tb_user (
	email, pass, name, phone, resident, address
) values (
	#{email}, #{pass}, #{name}, #{phone}, #{resident}, #{address}
);

mapper.update:
update tb_user 
   set title = #{title},
       content = #{content}
 where no = #{no}
