mapper.insert:
insert into tb_board (
no, title, writer, content
) values (
seq_board_no.nextval, #{title}, #{writer}, #{content}
)

mapper.update:
update tb_board
set view_cnt = view_cnt + 1
where no = #{no}

update tb_board
set title = #{title},
content = #{content}
where no = #{no}

mapper.insert:
