mapper.insert:
insert into tb_qna (
	 qnaTitle, qnaWriter, qnaContent
) values (
	 #{qnaTitle}, #{qnaWriter}, #{qnaContent}
)

mapper.update:
update tb_qna 
   set viewCnt = viewCnt + 1
 where qnaNo = #{qnaNo}

update tb_qna
   set qnaTitle = #{qnaTitle},
       qnaWriter = #{qnaWriter}, 
       qnaContent = #{qnaContent}
 where qnaNo = #{qnaNo}
