function head(){
if ($(this).scrollTop() > 100) { //752 서브비주얼사이즈
$('#header').addClass('fix');
}else{
$('#header').removeClass('fix');
} 
}

$(window).scroll(function () { head();});
$(window).ready(function () { head();});

var lastScrollTop = 0;
$(window).scroll(function(event){
var st = $(this).scrollTop();
if (st < lastScrollTop){
$('#header').addClass('up');
} else {
$('#header').removeClass('up');
}
lastScrollTop = st;
});
